select (sum(vl_saldo_inicial) - sum(vl_saldo_final)) as despesas, financeiro.reg_ans, relatorio.razao_social from relatorio inner join financeiro on relatorio.registro_ans = financeiro.reg_ans where descricao = 'EVENTOS/ SINISTROS CONHECIDOS OU AVISADOS  DE ASSISTÊNCIA A SAÚDE MEDICO HOSPITALAR ' and data >= '2024-10-1' GROUP BY financeiro.reg_ans, relatorio.registro_ans  order by despesas limit 10;